function accordion(t,e=!1){const i=document.querySelectorAll(t);for(let t of i){let l=t.firstElementChild.className+"--active",s=t.lastElementChild,o=t.firstElementChild;s.style.maxHeight=0,s.style.overflow="hidden",s.style.transition="max-height .3s ease-in-out",o.addEventListener("click",(function(){if(!0===e)for(let t of i)t.firstElementChild.classList.remove(l),t.lastElementChild.style.maxHeight=0;this.classList.toggle(l),this.classList.contains(l)?s.style.maxHeight=s.scrollHeight+"px":s.style.maxHeight=0}))}}accordion(".accordion__item",!0);